import React, {Component} from 'react'
import {Navbar, Button, Nav,} from 'react-bootstrap';
import { LinkContainer } from "react-router-bootstrap";
import { Input , Icon, Search } from 'semantic-ui-react'
import {BiPaperPlane} from "react-icons/bi"
import {FaUserAlt} from "react-icons/fa"
import {TiHome} from "react-icons/ti"
import {FaHandsHelping} from "react-icons/fa"
import axiosInstance from "../User/axiosApi";
import SearchResults from "../APIComponents/SearchResults";
import _ from "lodash";

const initialState = {
  loading: false, 
  results: [], 
  value: '',
}

function searchReducer(state, action) {
  switch (action.type) {
    case "CLEAN_QUERY":
      return initialState 
    case "START_SEARCH":
      return { ...state, loading: true, value: action.query}
    case "FINISH_SEARCH":
      return { ...state, loading: false, results: action.results }
    case "UPDATE_SELECTION":
      return { ...state, value: action.selection }

    default: 
      throw new Error() 
  }
}

let source = getSearchResults(); 

async function getSearchResults() {
  let results = await SearchResults();
  source = results; 
}

const NavBar = (props) => {

  // const [source, setSource] = React.useState([]);
  const [state, setState] = React.useReducer(searchReducer, initialState)
  const { loading, results, value } = state 

  // async function getSearchResults() {

  //   let results = await SearchResults(); 

  //   return results; 
  // }

  async function handleLogout() {
    try {
      const response = await axiosInstance.post('logout/', {
        "refresh_token": localStorage.getItem("refresh_token")
      });
      localStorage.removeItem("access_token");
      localStorage.removeItem("refresh_token");
      axiosInstance.defaults.headers['Authorization'] = null; 
      return response; 
    }
    catch (e) {
      console.log(e);
    }
  }

  const timeoutRef = React.useRef()
  const handleSearchChange = React.useCallback((e, data) => {
    clearTimeout(timeoutRef.current)
    setState({ type: 'START_SEARCH', query: data.value })

    timeoutRef.current = setTimeout(() => {
      if (data.value.length === 0) {
        setState({ type: 'CLEAN_QUERY' })
        return
      }

      const re = new RegExp(_.escapeRegExp(data.value), 'i')
      const isMatch = (result) => re.test(result.title)

      console.log(source);

      setState({
        type: 'FINISH_SEARCH',
        results: _.filter(source, isMatch)
      })

    }, 300)
  }, [])

  React.useEffect(() => {
    // let result = getSearchResults(); 
    // result.then((result) => {
    //   setSource(result);
    //   console.log(result);
    // })
    return () => {
      clearTimeout(timeoutRef.current)
    }
  }, [])

  return (
    <div>
    <Navbar bg="primary" variant="dark" fixed="top">
      <Navbar.Brand href="#home" className="pr-3">MatchMe</Navbar.Brand>


      {/* <Input icon='search' placeholder='Search...' /> */}

      <Search
        loading = {loading}
        onResultSelect={(e, data) => {
          setState({ type: 'UPDATE_SELECTION', selection: data.result.title })
        }} 
        onSearchChange = {handleSearchChange}
        result={results}
        value={value}
        placeholder = "Find users and groups!"
      />


      <Nav className="ml-auto">
        <LinkContainer to="/">
          <Nav.Link >
          <div style={{ textAlign: "center", marginBottom: 2}}>
            <TiHome size={25}/>
          </div>
          Home
          </Nav.Link>
        </LinkContainer>
        <LinkContainer to="/">
          <Nav.Link >
          <div style={{ textAlign: "center", marginBottom: 2}}>
            <FaHandsHelping size={25}/>
          </div>
          Groups
          </Nav.Link>
        </LinkContainer>
        <LinkContainer to="/feed">
          <Nav.Link >
          <div style={{ textAlign: "center", marginBottom: 2}}>
            <BiPaperPlane size={25}/>
          </div>
          Message
          </Nav.Link>
        </LinkContainer>
        <LinkContainer to="/profile" >
        <Nav.Link>
          <div style={{ textAlign: "center", marginBottom: 2}}>
          <FaUserAlt style={{fontSize: 25}}/>
          </div>
          Profile
          </Nav.Link>
      </LinkContainer>
      </Nav>


    </Navbar>
    <div style={{paddingTop: 120}}></div>
  </div>
  )

}


{/* <button onClick={this.handleLogout}>Logout</button> */}

export default NavBar;
