import React, {useState} from 'react'

import {Button, Icon, Label, Progress, Card, Image, List, Segment} from 'semantic-ui-react';

import Comments from "../Components/Comments";



const FeedCard = (props) => {
    const [seeComments, setSeeComments] = React.useState(false);

    const handleExpand = () => {
        setSeeComments(!seeComments);
    };


//     <Card>
//     <Card.Content header='Suggested Groups' />
//     <Card.Content extra>
//       <div class="ui middle aligned selection list">
//         {props.groups.map(group => (
//           <div class="item">
//             <img class="ui avatar image" src={group.image}/>
//             <div class="content">
//         <div class="header">{group.name}</div>
//             </div>
//           </div>
//         ))}
//       </div>
//     </Card.Content>
// </Card>


    if (props.isMatch) {
        return (
                <Card 
                    style={{width: "100%"}}
                >
                <Label attached="bottom">Action</Label>
                    <Card.Content>
                        <Card.Header>
                            <List horizontal selection relaxed>
                                <List.Item>
                                    <Image avatar src={props.post.profile.image} size="small" />
                                    <List.Content>
                                        <List.Header as='a'>
                                            {props.post.author.first_name + " " + props.post.author.last_name}
                                        </List.Header>
                                        <List.Description as='a'>
                                            {"Posted in " + props.post.group.name}
                                        </List.Description>
                                    </List.Content>
                                </List.Item>
                            </List>
                        </Card.Header>

                        <Card.Description style={{padding: 10,}}>
                            <p style={{"font-size": "15px"}}>
                            {props.post.content}
                            </p>
                        </Card.Description>

                        <br></br>

                    </Card.Content>

                    <Card.Content extra>
                        <List horizontal selection>
                            <List.Item>
                                <List.Icon 
                                name="heart outline" 
                                size="large"
                                label={{as: 'a', corner: 'left', value: props.post.hearts}}
                                >

                                </List.Icon>
                            </List.Item>
                            <List.Item onClick={handleExpand}>
                                <List.Icon name='comments outline' size='large' />
                            </List.Item>
                            <List.Item>
                                <List.Icon name='handshake outline' size='large' />
                            </List.Item>

                        </List>

                    </Card.Content>                    

                    <Comments props={props.post.comments} visible={seeComments}/>


                </Card>
        )
    } else {
        return (
            <div>test</div>
        )
    }
}

const styles = {
    card: {
        width: "100%",

    },
}

export default FeedCard;
