import React, {useState} from 'react'
import { Card, Icon, Grid, Label, Feed} from 'semantic-ui-react'
import LabelWIm from "./LabelWIm";
import { HiDotsVertical} from "react-icons/hi";
import ClearButton from './ClearButton';
import Comments from './Comments';
import Reveal from "./Reveal";


const FeedCard = (props) => {
    const [likes, updateLikes] = useState(props.likes);
    return(
            <Card style={{width: "100%"}}>
                <Card.Content header='header' >
                    <div style={{display: "flex", justifyContent: "space-between"}}>
                    <LabelWIm src={require("../Images/man.png")} username="Joe" />
                    <ClearButton>
                    <a><HiDotsVertical size="20" style={{alignSelf: "center"}}/></a>
                    </ClearButton>
                    </div>
                </Card.Content>
                <Card.Content description={props.description} />
                <Card.Content extra>
                <Grid className="pl-4">
                    <Grid.Row>
                    <Grid.Column width={2} style={{right: 4}}>
                        {/* like button */}
                        <Feed>
                            <Feed.Event>
                            <Feed.Content>
                            <Feed.Meta>
                            <Feed.Like style={{textDecoration: "none", fontSize: 14}}>
                                <Icon name='like' />   {likes}
                            </Feed.Like>
                            </Feed.Meta>
                            </Feed.Content>
                            </Feed.Event>
                            </Feed>
                    </Grid.Column>
                    <Grid.Column width={3} style={{paddingTop: 6}}>
                        <Icon name='share' />  Share
                    </Grid.Column>
                    {/* <Grid.Column width={3} style={{paddingTop: 6}}>
                        <Icon name='save' />  Save
                    </Grid.Column> */}
                    {/* <Grid.Column width={4}></Grid.Column> */}
                    </Grid.Row>
                    {/* <Grid.Column width={4} style={{top: -3, left: 26}}> */}                    
                </Grid>
                </Card.Content>
                <Card.Content extra>
                <Reveal labelName="Comments" iconName="angle double down">
                        <Comments />
                    </Reveal> 
                </Card.Content>
            </Card>

    );
}


