import React, {useState, useEffect} from 'react'
import FeedCard from "../Components/FeedCard";
import NavBar from "../Components/NavBar";
import { Card, Icon, Grid, Segment} from 'semantic-ui-react'
import GroupsCard from "../Components/GroupsCard";
import ProfileCard from "../Components/ProfileCard"
import NewPost from "../Components/NewPost";
import SuggestedGroups from '../Components/SuggestedGroups';
import {Modal, Button, Nav} from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import GroupPosts from "../APIComponents/GroupPosts";
import UserGroups from "../APIComponents/UserGroups";
import UserInfo from "../APIComponents/UserInfo";
import ProfileInfo from "../APIComponents/ProfileInfo";
import GroupSuggestions from "../APIComponents/GroupSuggestions";


const description = [
  'Amy is a violinist with 2 years experience in the wedding industry.',
  'She enjoys the outdoors and currently resides in upstate New York.',
].join(' ')

function postReducer(state, action) {
  switch (action.type) {
    case 'OPEN_MODAL' :
      return { showpost: true, dimmer: action.dimmer }
    case 'CLOSE_MODAL' : 
      return { showpost: false }
    default: 
      throw new Error(); 
  }
}


const Home = (props) => {

  const [state, handlePost] = React.useReducer(postReducer, {
    showpost: false, 
    dimmer: undefined,
  })

  function handlePostChange(state) {
    handlePost(state);
  }

  const [match_state, handleMatchPost] = React.useReducer(postReducer, {
    showpost: false, 
    dimmer: undefined, 
  })

  function handleMatchPostChange(state) {
    handleMatchPost(state);
  }


  const [user, setUser] = useState({});
  const [profile, setProfile] = useState({});
  const [groups, setGroups] = useState([]);
  const [suggestions, setSuggestions] = useState([]); // group suggestions 
  const [posts, setPosts] = useState([]);

  const user_id = localStorage.getItem('user_id');


  useEffect(() => {
      getPosts(user_id);
      getUser(user_id);
      getProfile(user_id);
      getSuggestions(user_id);
  }, {});

  async function getSuggestions(user_id) {
    let suggestions = GroupSuggestions(user_id);
    suggestions.then(data => {
      setSuggestions(data);
    })

  }

  async function getPosts(user_id) {
    await getGroups(user_id);

    let arr = Array(); 

    for (let idx in groups) {
      let group = groups[idx];

      let data = await GroupPosts(group.id);
      data.then(info => {
        arr.push(info);
      })
    }

    setPosts(arr);
  }

  async function getGroups(user_id) {
    let groups = UserGroups(user_id);
    groups.then(data => {
      setGroups(data);
    })
  }

  async function getUser(user_id) {
    let user = UserInfo(user_id);

    user.then(data => {
      setUser(data);
    })
  };

  async function getProfile(user_id) {
    let profile = ProfileInfo(user_id);

    profile.then(data => {
      setProfile(data);
    })
  }

    return (
      <div>
          <NavBar />

          <Grid centered>
            <Grid.Row>
                <Grid.Column width={2} >
                  <LinkContainer to="/profile">

                      <ProfileCard className="profCard" name={user.first_name + " " + user.last_name} src={profile.image} 
                      inspired={profile.total_matches} matched={profile.ind_matches} 
                      groups={groups} />


                    </LinkContainer>

                    <GroupsCard name={props.name} src={props.src} inspired={props.inspired} matched={props.matched} groups={groups}/>

                </Grid.Column>



                  <Grid.Column centered width={3}>

                    <NewPost profile = {profile} match_state = {match_state} state={ state } handlePostChange={handlePostChange} handleMatchPostChange={handleMatchPostChange} />



                    <FeedCard description={description} likes={8}/>



                  </Grid.Column>


                  <Grid.Column width={2}>

                    <SuggestedGroups groups={suggestions}/>



                  </Grid.Column>

              </Grid.Row>

          </Grid>

      </div>

    );
}

export default Home;
