import React, { Component } from "react";
import axiosInstance from "../User/axiosApi";

class Register extends Component{
    constructor(props){
        super(props);
        this.state = {
            username: "",
            first_name: "",
            last_name: "",
            password: "",
            email:"",
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('api/auth/register/', {
                username: this.state.username,
                email: this.state.email,
                password: this.state.password,
                first_name: this.state.first_name,
                last_name: this.state.last_name
            });
            return response;
        } catch (error) {
            console.log(error.stack);
            this.setState({
                errors:error.response.data
            });
        }
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h3>Register</h3>

                    <div className="form-group">
                        <label>Username</label>
                        <input type="text" className="form-control" value={this.state.username} placeholder="Username" onChange={this.handleChange}/>
                    </div>

                    <div className="form-group">
                        <label>First name</label>
                        <input type="text" className="form-control" value={this.state.first_name}  placeholder="First name" onChange={this.handleChange}/>
                    </div>

                    <div className="form-group">
                        <label>Last name</label>
                        <input type="text" className="form-control" value={this.state.last_name}  placeholder="Last name" onChange={this.handleChange}/>
                    </div>

                    <div className="form-group">
                        <label>Email address</label>
                        <input type="email" className="form-control" value={this.state.email}  placeholder="Enter email" onChange={this.handleChange}/>
                    </div>

                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" className="form-control" value={this.state.password}  placeholder="Enter password" onChange={this.handleChange}/>
                    </div>

                    <button type="submit" value="submit" className="btn btn-primary btn-block">Sign Up</button>
                    <p className="forgot-password text-right">
                        Already registered <a href="#">sign in?</a>
                    </p>
                </form>
            </div>
        )
    }
}
export default Register;
