import React, {useState} from 'react'

import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import {Button, Icon, Label, Progress} from 'semantic-ui-react';



const FeedCard = (props) => {
    const [seeComments, setSeeComments] = React.useState(false);

    const handleExpand = () => {
        setSeeComments(!seeComments);
    };

    if (props.isMatch) {
        return (
                    <Card className={styles.card}>

                    <Label as='a' color='red' ribbon>
                        Action
                    </Label>

                    <CardHeader
                    avatar={<Avatar aria-label="recipe" src={props.post.profile.image}/>}
                    action={
                        <IconButton aria-label="settings">
                        </IconButton>
                    }
                    title={props.post.author.first_name + " " + props.post.author.last_name}
                    subheader={"Posted in " + props.post.group.name}
                    />

                    <CardContent>
                    <Typography variant="body2" color="textSecondary" component="p">
                        {props.post.content}
                    </Typography>
                    </CardContent>
                    <CardActions disableSpacing>
                    <IconButton> {/* HEART */}

                        <Button as="div" labelPosition="right">
                            <Button color="red">
                                <Icon name='heart' />
                                Heart
                            </Button>
                            <Label as='a' basic color='red' pointing='left'>
                                {props.post.hearts}
                            </Label>
                        </Button>

                    </IconButton>


                    <IconButton>

                        <Button as="div" labelPosition="right">
                            <Button color="blue">
                                <Icon name='handshake outline' />
                                Match 
                            </Button>
                            <Label as='a' basic color='blue' pointing='left'>
                                {props.post.matches}
                            </Label>
                        </Button>

                    </IconButton>

                    <Progress percent={((props.post.matches / props.post.goal) * 100).toFixed(3)} autoSuccess/>

                    {/* <IconButton
                        className={clsx(classes.expand, {
                        [classes.expandOpen]: expanded,
                        })}
                        onClick={handleExpandClick}
                        aria-expanded={expanded}
                        aria-label="show more"
                    >
                        <ExpandMoreIcon />
                    </IconButton> */}
                    </CardActions>
                        {/* <Collapse in={expanded} timeout="auto" unmountOnExit>
                        <CardContent>

                        </CardContent>
                        </Collapse> */}
                    </Card>
        )
    } else {
        return (
            <div>test</div>
        )
    }
}

const styles = {
    card: {
        maxWidth: 345,
    },
}

{/* <div>
<h1>MATCH POST</h1>
