import { render } from "react-dom";
import axiosInstance from "../User/axiosApi";
import ParsePosts from "../APIComponents/ParsePosts";
import { Button } from 'semantic-ui-react'; 
import FeedCard from "./FeedCard";
import { BiBorderNone } from "react-icons/bi";

function Posts() {

    return (
        <div>

            {props.matches.map(match => ( // group.name, group.description, group.image, group.theme

                <FeedCard isMatch={true} post={match} className={this.props.showMatches ? '' : 'hidden'}/>

            ))
            }

            {props.posts.map(post => {

                <FeedCard isMatch={false} post={post} className={this.props.showMatches ? 'hidden' : ''} />

            })}

            {/* </InfiniteScroll> */}
        </div>
    );

}

export default Posts;
