import React, {Component} from 'react'
import NavBar from "../Components/NavBar";
import axiosInstance from "../User/axiosApi";
import { Icon} from 'semantic-ui-react'
import * as Color from "../Components/Color";

const description = [
  'I am a violinist with 2 years of experience in the wedding industry.',
  'I enjoy the outdoors and currently reside in upstate New York.',
].join(' ')


class Profile extends Component {
  constructor() {
    super();
    this.handleLogout = this.handleLogout.bind(this);
}

  async handleLogout() {
      try {
          const response = await axiosInstance.post('/blacklist/', {
              "refresh_token": localStorage.getItem("refresh_token")
          });
          localStorage.removeItem('access_token');
          localStorage.removeItem('refresh_token');
          axiosInstance.defaults.headers['Authorization'] = null;
          return response;
      }
      catch (e) {
          console.log(e);
      }
  };

  render (){
    return (
      <div>
        <NavBar />
        <a onClick={this.handleLogout}>
          <Icon name="sign-out" style={{fontSize: 45, color: Colors.primary}}/>
        </a>
        <div style={{textAlign: "center"}}>
          <img src={this.props.src} style={{width: 350}}/>
        </div>
        <div style={{textAlign: "center", fontSize: 40, }}>
          {this.props.username}
        </div>
        <div style={{textAlign: "center", marginRight: "auto", marginLeft: "auto", fontSize: 25, width: 900}}>
          {description}
        </div>
      </div>

    );
  }
}

export default Profile;
