import React, { Component, useState, useEffect } from "react";
import axiosInstance from "../User/axiosApi";
import { Modal, Button, Card, Form } from 'semantic-ui-react';
import { useHistory } from "react-router-dom";
import {AiFillPicture} from "react-icons/ai";
import {FiLink} from "react-icons/fi";
import {BsFillCameraVideoFill} from "react-icons/bs"

const NewPost = (props) => {

    let history = useHistory();

    const[options, setOptions] = useState([]);
    const[form, setForm] = useState({
        group: null,
        content: "",
    });

    useEffect(() => {
        parseOptions()
    }, []);   

    async function parseOptions() {

        var groups = await groupsFetch();

        var options_arr = new Array();  

        for (let idx in groups) { // this code makes no sense! :) but it works...
            // console.log(group);
            options_arr.push({
                text: groups[idx].name, 
                value: groups[idx].id,
            });
        }

        console.log(options_arr);

        setOptions(options_arr);
    };

    async function groupsFetch() {
        try {
            var id = localStorage.getItem("user_id");
            const response = await axiosInstance.get('api/groups/user/' + id.toString() + "/");

            return response.data;

        } catch (error) {
            console.log(error);
        }
    };

    async function handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('api/posts/create/', {
                group: form.group,
                content: form.content,
            });
            if (response.status === 200) {
                history.push('/');
            }

            console.log(response);

            return response;
        } catch (error) {
            console.log(error.stack);
        }
    };

    function handleChange(event) {
        const obj = {...form};
        obj.content = event.target.value;
        setForm(obj);
    }

    const handleSelectChange = (e, { value }) => {
        const obj = {...form};
        obj.group = value; 
        setForm(obj);
    }

    return (
        <div>
            test
        </div>
    );
}

export default NewPost;
