import React, { Component, useState, useEffect } from "react";
import axiosInstance from "../User/axiosApi";
import { Modal, Card, Form } from 'semantic-ui-react';
import { Button } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import {AiFillPicture, AiFillPropertySafety} from "react-icons/ai";
import {FiLink} from "react-icons/fi";
import {BsFillCameraVideoFill} from "react-icons/bs"

const NewPost = (props) => {

    let history = useHistory();

    const[options, setOptions] = useState([]);
    const[form, setForm] = useState({
        group: null,
        content: "",
    });

    useEffect(() => {
        parseOptions()
    }, []);   

    async function parseOptions() {

        var groups = await groupsFetch();

        var options_arr = new Array();  

        for (let idx in groups) { // this code makes no sense! :) but it works...
            // console.log(group);
            options_arr.push({
                text: groups[idx].name, 
                value: groups[idx].id,
            });
        }

        console.log(options_arr);

        setOptions(options_arr);
    };

    async function groupsFetch() {
        try {
            var id = localStorage.getItem("user_id");
            const response = await axiosInstance.get('api/groups/user/' + id.toString() + "/");

            return response.data;

        } catch (error) {
            console.log(error);
        }
    };

    async function handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('api/posts/create/', {
                group: form.group,
                content: form.content,
            });

            if (response.status === 200) {
                // history.push('/');
                props.handlePostChange({type: 'CLOSE_MODAL'})
            }

            console.log(response);

            return response;
        } catch (error) {
            console.log(error.stack);
        }
    };

    function handleChange(event) {
        const obj = {...form};
        obj.content = event.target.value;
        setForm(obj);
    }

    const handleSelectChange = (e, { value }) => {
        const obj = {...form};
        obj.group = value; 
        setForm(obj);
    }


//     <div class="ui middle aligned selection list">
//     {props.groups.map(group => (
//       <div class="item">
//         <img class="ui avatar image" src={group.image}/>
//         <div class="content">
//     <div class="header">{group.name}</div>
//         </div>
//       </div>
//     ))}
//   </div>


    return (
        <div>
            <Card style={{flexDirection: "row", justifyContent:"center", width: "100%", paddingTop: 5, paddingBottom: 5, marginBottom: 25}} >
                <Button fluid onClick={() => props.handlePostChange({type: 'OPEN_MODAL' })}>Talk to the World!</Button>
                {/* <a style={{color:"gray", marginTop: "auto", marginBottom: "auto", marginLeft: 30}}><FiLink size={30} className="attachButton"/></a>
                <a style={{color:"gray", marginTop: "auto", marginBottom: "auto", marginLeft: 18}}><AiFillPicture size={35} className="attachButton"/></a>
                <a style={{color:"gray", marginTop: "auto", marginBottom: "auto", marginLeft: 18}}><BsFillCameraVideoFill size={30} className="attachButton"  /></a> */}
            </Card>

            <Modal 
                dimmer={props.state.dimmer}
                open={props.state.showpost}
                onClose = {() => props.handlePostChange({ type: 'CLOSE_MODAL' })}
            >
                <Modal.Header>
                    New Post
                </Modal.Header>

                <Modal.Content>
                    <Form onSubmit={(e) => handleSubmit(e)} id="new-post-form">
                        <Form.Group widths='equal'>
                            <Form.Select
                                fluid 
                                label='Group'
                                name='group'
                                options={options}
                                placeholder='Group'
                                // value = {form.group}
                                onChange = {handleSelectChange}
                            />
                        </Form.Group>
                        <Form.TextArea required name='content' label="Content" placeholder="Talk to the World!" onChange = {(e) => handleChange(e)}/>
                    </Form>
                </Modal.Content>
                <Modal.Actions>
                        <Button variant="secondary" onClick={() => props.handlePostChange({type: 'CLOSE_MODAL' })}>Close</Button>
                        <Button variant="primary" type="submit" value="submit" form="new-post-form">Post</Button>
                </Modal.Actions>

            </Modal>
        </div>
    );
}

export default NewPost;
