import React, {useState, useEffect} from 'react'
import FeedCard from "../Components/FeedCard";
import NavBar from "../Components/NavBar";
import { Card, Icon, Grid} from 'semantic-ui-react'
import GroupsCard from "../Components/GroupsCard";
import ProfileCard from "../Components/ProfileCard"
import NewPost from "../Components/NewPost";
import PopularGroups from '../Components/PopularGroups';
import {AiFillPicture} from "react-icons/ai";
import {FiLink} from "react-icons/fi";
import {BsFillCameraVideoFill} from "react-icons/bs"
import {Modal, Button, Nav} from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import GroupPosts from "../APIComponents/GroupPosts";
import UserGroups from "../APIComponents/UserGroups";


const description = [
  'Amy is a violinist with 2 years experience in the wedding industry.',
  'She enjoys the outdoors and currently resides in upstate New York.',
].join(' ')


const Home = (props) => {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);



  const [groups, setGroups] = useState([]);
  const [posts, setPosts] = useState([]);

  const user_id = localStorage.getItem('user_id');


  useEffect(() => {
      getPosts(user_id);
  }, {});

  async function getPosts(user_id) {
    await getGroups(user_id);

    let arr = Array(); 

    for (let idx in groups) {
      let group = groups[idx];

      let data = await GroupPosts(group.id);
      data.then(info => {
        arr.push(info);
      })
    }

    setPosts(arr);
  }

  async function getGroups(user_id) {
    let groups = UserGroups(user_id);
    groups.then(data => {
      setGroups(data);
    })
  }



    return (
        <div>
          <NavBar />
          <div style={{width: "90%", marginRight: "auto", marginLeft: "auto", marginTop: 20}}>
        <Grid centered>
        <Grid.Row>
        <Grid.Column width={3} >
          <LinkContainer to="/profile">
            <Nav.Link >
              <ProfileCard className="profCard" name={props.name} src={props.src} 
              inspired={props.inspired} matched={props.matched} 
              groups={props.groups} />
            </Nav.Link>
            </LinkContainer>
          </Grid.Column>
          <Grid.Column width={8}>
            <Card style={{flexDirection: "row", width: "100%", paddingTop: 5, paddingBottom: 5, marginBottom: 25}} >
              <img src={props.src} style={{width: 65, marginLeft: 15, marginRight:20}}/>
              <div style={{marginTop: "auto", marginBottom: "auto"}}>
              <a onClick={handleShow} className="newPostButton" >Talk to the World!</a>
              </div>
              <a style={{color:"gray", marginTop: "auto", marginBottom: "auto", marginLeft: 30}}><FiLink size={30} className="attachButton"/></a>
              <a style={{color:"gray", marginTop: "auto", marginBottom: "auto", marginLeft: 18}}><AiFillPicture size={35} className="attachButton"/></a>
              <a style={{color:"gray", marginTop: "auto", marginBottom: "auto", marginLeft: 18}}><BsFillCameraVideoFill size={30} className="attachButton"  /></a>
            </Card>
            <FeedCard description={description} likes={8}/>
          </Grid.Column>
          <Grid.Column width={4}>
            <PopularGroups />
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
        <Grid.Column width={3}>

            <GroupsCard name={props.name} src={props.src} inspired={props.inspired} matched={props.matched} groups={groups}/>

          </Grid.Column>
          <Grid.Column width={8}>
            <FeedCard description={description} likes={8}/>
          </Grid.Column>
          <Grid.Column width={4}>
          </Grid.Column>
        </Grid.Row>
      </Grid>
        </div>
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>New Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <NewPost />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Post
          </Button>
        </Modal.Footer>
      </Modal>
      </div>

    );
}

export default Home;
