import React, {useState} from 'react'

import {Button, Icon, Label, Progress, Card, Image} from 'semantic-ui-react';



const FeedCard = (props) => {
    const [seeComments, setSeeComments] = React.useState(false);

    const handleExpand = () => {
        setSeeComments(!seeComments);
    };

    if (props.isMatch) {
        return (
            <Card style={{width: "100%"}}>
                <Label as='a' color='red' ribbon>
                    Action
                </Label>

                <Card.Content>
                    <Image 
                        floated="right"
                        size="mini"
                        src={props.post.profile.image}
                    />
                    <Card.Header>{props.post.author.first_name + " " + props.post.author.last_name}</Card.Header>
                    <Card.Meta>{"Posted in " + props.post.group.name}</Card.Meta>
                    <Card.Description>
                        {props.post.content}
                    </Card.Description>

                </Card.Content>
                <Card.Content extra>
                    <div className="ui two buttons">
                        <Button as="div" labelPosition="right">
                            <Button color="red">
                                <Icon name='heart' />
                                Heart
                            </Button>
                            <Label as='a' basic color='red' pointing='left'>
                                {props.post.hearts}
                            </Label>
                        </Button>
                    </div>
                    <Progress percent={((props.post.matches / props.post.goal) * 100).toFixed(3)} autoSuccess/>
                </Card.Content>                    
            </Card>
        )
    } else {
        return (
            <div>test</div>
        )
    }
}

const styles = {
    card: {
        width: "100%",
    },
}

