import React, { Component } from "react";
import axiosInstance from "../User/axiosApi";
import { withRouter } from "react-router-dom";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {username: "", password: "", password2: ""};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('api/auth/token/obtain/', {
                username: this.state.username,
                password: this.state.password
            });
            axiosInstance.defaults.headers['Authorization'] = "JWT " + response.data.access;

            localStorage.setItem('access_token', response.data.access);
            localStorage.setItem('refresh_token', response.data.refresh);

            const response2 = await axiosInstance.get('api/auth/user/id/');

            localStorage.setItem('user_id', response2.data);

            if (response.status === 200) {
                this.props.history.push("/feed");
            }

            return response.data;
        } catch (error) {
            throw error;
        }
    }

    render() {
        return (
            <div class="container justify-content-center align-items-center">
                <div class="row justify-content-center">
                    <div class="col-md-6" style={styles.login_form}>
                        <h3>Log In</h3>
                        <form onSubmit={this.handleSubmit}>
                            <div class="form-group">
                                <input name="username" type="username" className="form-control" placeholder="Username" onChange={this.handleChange} value={this.state.username}/>
                            </div>
                            <div class="form-group">
                                <input name="password" type="password" className="form-control" placeholder="Password" onChange={this.handleChange} value={this.state.password}/>
                            </div>
                            <div class="form-group">
                                <input name="password2" type="password" className="form-control" placeholder="Confirm Password" onChange={this.handleChange} value={this.state.password2}/>
                            </div>
                            <button type="submit" className="btn btn-primary btn-block">Submit</button>
                            <p className="forgot-password text-right">
                                Need an account? <a href="/register">Register</a>
                            </p>
                        </form>
                    </div>
                </div>
            </div>    
        )
    }
}

const styles = {
    login_form: {
        "box-shadow": "0 5px 8px 0 rgba(0, 0, 0, 0.2), 0 9px 26px 0 rgba(0, 0, 0, 0.19);"
    },
};


export default withRouter(Login);
