import React, { Component } from "react";
import axiosInstance from "../User/axiosApi";

class Register extends Component{
    constructor(props){
        super(props);
        this.state = {
            username: "",
            first_name: "",
            last_name: "",
            password: "",
            email:"",
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('api/auth/register/', {
                username: this.state.username,
                email: this.state.email,
                password: this.state.password
            });
            return response;
        } catch (error) {
            console.log(error.stack);
            this.setState({
                errors:error.response.data
            });
        }
    }

    render() {
        return (
            <div>
                Register
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Username:
                        <input name="username" type="text" value={this.state.username} onChange={this.handleChange}/>
                        { this.state.errors.username ? this.state.errors.username : null}
                    </label>
                    <label>
                        First Name:
                        <input name="first_name" type="text" value={this.state.first_name} onChange={this.handleChange}/>
                        { this.state.errors.first_name ? this.state.errors.first_name : null}
                    </label>
                    <label>
                        Last Name:
                        <input name="last_name" type="text" value={this.state.last_name} onChange={this.handleChange}/>
                        { this.state.errors.last_name ? this.state.errors.last_name : null}
                    </label>
                    <label>
                        Email:
                        <input name="email" type="email" value={this.state.email} onChange={this.handleChange}/>
                        { this.state.errors.email ? this.state.errors.email : null}
                    </label>
                    <label>
                        Password:
                        <input name="password" type="password" value={this.state.password} onChange={this.handleChange}/>
                        { this.state.errors.password ? this.state.errors.password : null}
                    </label>
                    <input type="submit" value="Submit"/>
                </form>
            </div>
        )
    }
}
export default Register;
