import React, {Component, useState} from 'react'
import {Navbar, Button, Nav,} from 'react-bootstrap';
import { LinkContainer } from "react-router-bootstrap";
import { Icon, Search} from 'semantic-ui-react'
import {BiPaperPlane} from "react-icons/bi"
import {FaUserAlt} from "react-icons/fa"
import {TiHome} from "react-icons/ti"
import {FaHandsHelping} from "react-icons/fa"
import axiosInstance from "../User/axiosApi";
import SearchResults from "../APIComponents/SearchResults";
import _ from "lodash";
import DropDown from "./DropDown";
import InputBase from '@material-ui/core/InputBase';
import SearchIcon from '@material-ui/icons/Search'
import { fade, makeStyles } from '@material-ui/core/styles'
import AllGroups from "../APIComponents/AllGroups";

const initialState = {
  loading: false, 
  results: [], 
  value: '',
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white'
  },
  inputRoot: {
    color: 'white',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));



function searchReducer(state, action) {
  switch (action.type) {
    case "CLEAN_QUERY":
      return initialState 
    case "START_SEARCH":
      return { ...state, loading: true, value: action.query}
    case "FINISH_SEARCH":
      return { ...state, loading: false, results: action.results }
    case "UPDATE_SELECTION":
      return { ...state, value: action.selection }

    default: 
      throw new Error() 
  }
}

let source = getSearchResults(); 

async function getSearchResults() {
  let results = await SearchResults();
  source = results; 
}

const NavBar = (props) => {
  const classes = useStyles();
  // const [source, setSource] = React.useState([]);
  const [state, setState] = React.useReducer(searchReducer, initialState)
  const { loading, results, value } = state 

  // async function getSearchResults() {

  //   let results = await SearchResults(); 

  //   return results; 
  //  }

  async function handleLogout() {
    try {
      const response = await axiosInstance.post('logout/', {
        "refresh_token": localStorage.getItem("refresh_token")
      });
      localStorage.removeItem("access_token");
      localStorage.removeItem("refresh_token");
      axiosInstance.defaults.headers['Authorization'] = null; 
      return response; 
    }
    catch (e) {
      console.log(e);
    }
  }

  const timeoutRef = React.useRef()
  const handleSearchChange = React.useCallback((e, data) => {
    clearTimeout(timeoutRef.current)
    setState({ type: 'START_SEARCH', query: data.value })

    timeoutRef.current = setTimeout(() => {
      if (data.value.length === 0) {
        setState({ type: 'CLEAN_QUERY' })
        return
      }

      const re = new RegExp(_.escapeRegExp(data.value), 'i')
      const isMatch = (result) => re.test(result.title)

      console.log(source);

      setState({
        type: 'FINISH_SEARCH',
        results: _.filter(source, isMatch)
      })

    }, 300)
  }, [])

  async function getGroups() {
    let results = await AllGroups(); 
    return results; 
  }

  const [all_groups, setAllGroups] = useState([]);

  async function parseOptions(groups) {
    let arr = Array();
    for (let idx in groups) {
      arr.push({
        key: groups[idx].name,
        text: groups[idx].name,
        value: groups[idx].name,
        image: {avatar:true, src: groups[idx].image},
      })
    }
    setAllGroups(arr);
  }


  React.useEffect(() => {
    // let result = getSearchResults(); 
    // result.then((result) => {
    //   setSource(result);
    //   console.log(result);
    // })
    let data = getGroups();
    data.then((data) => {
      parseOptions(data)
    })
    // return () => {
    //   clearTimeout(timeoutRef.current)
    // }

  }, []);

  return (
    <div>
    <Navbar bg="primary" variant="dark" fixed="top">
    <div style={{marginLeft: 10}}>
        <FaHandsHelping size={30} color="white"/>
      </div>
    <LinkContainer to="/feed" >
    <Nav.Link >
      <Navbar.Brand href="#home" style={{fontSize: 23}}className="pr-2">MatchMe</Navbar.Brand>
      </Nav.Link >
    </LinkContainer>
      <div style={{marginRight: 20}}>

      <DropDown options={all_groups} />

      </div>
      <div style={{width: 500}}>
      <div className={classes.search}>
        <div className={classes.searchIcon}>
          <SearchIcon />
        </div>
        <InputBase
          placeholder="Search..."
          classes={{
            root: classes.inputRoot,
            input: classes.inputInput,
          }}
          inputProps={{ 'aria-label': 'search', color: 'white'}}
        />
      </div>


      </div>
      {/* <Input icon='search' placeholder='Search...' /> */}

      {/* <Search
        loading = {loading}
        onResultSelect={(e, data) => {
          setState({ type: 'UPDATE_SELECTION', selection: data.result.title })
        }} 
        onSearchChange = {handleSearchChange}
        result={results}
        value={value}
        placeholder = "Find users and groups!"
        size= {300}
      /> */}

      <Nav className="ml-auto">
        <LinkContainer to="/feed">
          <Nav.Link >
          <div style={{ textAlign: "center", marginBottom: 2}}>
            <TiHome size={25}/>
          </div>
          Home
          </Nav.Link>
        </LinkContainer>
        <LinkContainer to="/feed">
          <Nav.Link >
          <div style={{ textAlign: "center", marginBottom: 2}}>
            <BiPaperPlane size={25}/>
          </div>
          Message
          </Nav.Link>
        </LinkContainer>
        <LinkContainer to="/profile" >
        <Nav.Link>
          <div style={{ textAlign: "center", marginBottom: 2}}>
          <FaUserAlt style={{fontSize: 25}}/>
          </div>
          Profile
          </Nav.Link>
      </LinkContainer>
      </Nav>
    </Navbar>
    <div style={{paddingTop: 120}}></div>
  </div>
  )

}


{/* <button onClick={this.handleLogout}>Logout</button> */}

export default NavBar;
