import React, { Component, useState, useEffect } from "react";
import { render } from "react-dom";
import axiosInstance from "../User/axiosApi";
import GroupInfo from "../Components/GroupInfo";


function Posts() {
    const [posts, setPosts] = useState([]);
    const [matches, setMatches] = useState([]); 

    useEffect(() => {
        postFetch({ limit:10 });
    }, []);    

    async function postFetch(pagination) {
        try {
            var limit = pagination.limit; 
            var id = localStorage.getItem("user_id");
            const response = await axiosInstance.get('api/posts/interests/' + id.toString() + "/" + limit.toString() + "/");

            const data = response.data;

            var info = await GroupInfo(data[0].group)
            console.log(info);

            setPosts(data.filter(post => post.isMatch === false));
            setMatches(data.filter(post => post.isMatch === true));

            return response.data; 
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div>
            {/* <InfiniteScroll
                pageStart = {0}
                loadMore = {() => postFetch({ limit: posts.length + matches.length + 10 })}
                hasMore = {true || false}
                useWindow = {false}
                loader = {
                    <div key="loading" className="loader">
                        Loading...
                    </div>
                }
            >  */}
                {matches.map(match => (
                    <div>
                        <h1>MATCH POST</h1>
                        {match.content}
                        {match.hearts}
                        {match.author}
                        {match.group}
                        {match.matches}
                        {match.goal}
                    </div>
                ))

                }
                {posts.map(post => (
                    <div>
                        <h1>REGULAR POST</h1>
                        {post.content}
                        {post.hearts}
                        {post.author}
                        {post.group}
                    </div>
                ))}
            {/* </InfiniteScroll> */}
        </div>
    );

}

export default Posts;
