import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import NavBar from "../Components/NavBar";
import ParsePost from "../APIComponents/ParsePost";
import PostInfo from "../APIComponents/PostInfo";
import {Grid, Container, Header, Image, Label, Menu} from "semantic-ui-react";
import Comments from "../Components/Comments";

const IndPost = (props) => {

    const [showComments, setShowComments] = useState(false);
    const [post, setPost] = useState({});
    const post_id = props.postID;

    const handleShowComments = () => {
        setShowComments(true);
    }

    const handleHideComments = () => {
        setShowComments(false);
    }

    useEffect(() => {
        getPost();
    }, {});

    async function getPost() {
        let data = await PostInfo(post_id);
        console.log(data);
        data = await ParsePost(data);
        setPost(data);
    }
    if (post.isMatch) {
        return(
            <div>
                <NavBar />
                <Grid centered columns={2}>
                    <Grid.Column>
                        <Container text>
                            <Label as='a' color='blue' ribbon>
                                {post.group.theme}
                            </Label>
                            <Header as ='h1' style={{fontSize: '5em'}}>
                                {post.group.name}
                            </Header>
                            <Header as='h1'>
                                Match {post.author.first_name}'s lead.
                            </Header>
                        </Container>
                    </Grid.Column>
                    <Grid.Row centered columns={8}>
                        <Grid.Column>
                            <Grid.Row>
                                <Menu attached='top' tabular fluid widths={2}>
                                    <Menu.Item
                                        name='post'
                                        active={!showComments}
                                        onClick={handleHideComments}
                                    >
                                    </Menu.Item>
                                    <Menu.Item
                                        name='comments'
                                        active={showComments}
                                        onClick={handleShowComments}
                                    >
                                    </Menu.Item>
                                </Menu>
                            </Grid.Row>
                            <Grid.Row centered>
                                <Container text style={{display: showComments ? "none" : ""}}>
                                            <p>
                                                {post.content}
                                            </p>
                                </Container>
                                <Container text style={{display: showComments ? "" : "none"}}>
                                    <Comments comments={post.comments} />
                                </Container>
                            </Grid.Row>
                        </Grid.Column>


                        <Grid.Column centered>
                            <Text style={{fontWeight: "bold"}}>
                                {post.matches ? post.matches : 0} have matched. Help get to {post.goal}.
                            </Text>

                        </Grid.Column>
                    </Grid.Row>
                </Grid>
            </div>
        )
    } else {
        return(
            <div>
                under construction
            </div>
        )
    }

}

export default IndPost;
