import React, {useState} from 'react'

import {Button, Icon, Label, Progress, Card, Image, List} from 'semantic-ui-react';



const FeedCard = (props) => {
    const [seeComments, setSeeComments] = React.useState(false);

    const handleExpand = () => {
        setSeeComments(!seeComments);
    };


//     <Card>
//     <Card.Content header='Suggested Groups' />
//     <Card.Content extra>
//       <div class="ui middle aligned selection list">
//         {props.groups.map(group => (
//           <div class="item">
//             <img class="ui avatar image" src={group.image}/>
//             <div class="content">
//         <div class="header">{group.name}</div>
//             </div>
//           </div>
//         ))}
//       </div>
//     </Card.Content>
// </Card>


    if (props.isMatch) {
        return (
            <Card style={{width: "100%"}}>
                {/* <Label as='a' color='red' ribbon>
                        Action
                </Label> */}
                <Card.Content>
                    <Card.Header>
                        <div class="ui middle aligned selection list">
                            <div class="item">
                                <img class="ui avatar image" src={props.post.profile.image} />
                                <div class="content">
                                    <div class='header'>
                                        {props.post.author.first_name + " " + props.post.author.last_name}
                                    </div>
                                    <div>
                                        <small>{"Posted in " + props.post.group.name}</small>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Card.Header>
                    <Card.Description style={{padding: 10,}}>
                        <p>
                        {props.post.content}
                        </p>
                    </Card.Description>

                    <br></br>

                    <List horizontal selection relaxed>
                        <List.Item>
                            <Image avatar>
                                <List.Icon name="heart" size="large" />
                            </Image>
                            <List.Content>
                                <List.Header as='a'>Heart</List.Header>
                            </List.Content>
                        </List.Item>
                        <List.Item>
                            <Image avatar>
                                <List.Icon name='handshake outline' size='large' />
                            </Image>
                            <List.Content>
                                <List.Header as='a'>Match</List.Header>
                            </List.Content>

                        </List.Item>

                    </List>

                </Card.Content>
                <Card.Content extra>
                    <Progress 

                    size="tiny"

                    percent={((props.post.matches / props.post.goal) * 100).toFixed(3)} 

                    autoSuccess 

                    label = {"Help me hit my goal of " + props.post.goal + "actions!"}

                    />

                </Card.Content>                    
            </Card>
        )
    } else {
        return (
            <div>test</div>
        )
    }
}

const styles = {
    card: {
        width: "100%",

    },
}

export default FeedCard;
