import React, {useState} from 'react'

import {Button, Icon, Label, Progress, Card, Image, List, Container, Popup, Header} from 'semantic-ui-react';

import Comments from "../Components/Comments";

import {Link} from "react-router-dom";

import SubmitHeart from "../APIComponents/SubmitHeart";
import SubmitMatch from "../APIComponents/SubmitMatch";
import RemoveHeart from "../APIComponents/RemoveHeart";
import RemoveMatch from "../APIComponents/RemoveMatch";

const FeedCard = (props) => {

    const word_limit = 100;

    const [seeComments, setSeeComments] = React.useState(false);

    const [hearted, setHeart] = React.useState(props.post.hearted);

    const [matched, setMatch] = React.useState(props.post.isMatch ? props.post.matched : false);

    const handleExpand = () => {
        setSeeComments(!seeComments);
    };

    const post_id = props.post.id; 
    const user_id = props.curr_user; 

    async function handleCreateMatch() {
        let response = await SubmitMatch({
            author: user_id, 
            match_post: post_id, 
        })

        if (response.status === 201) {
            setMatch(true);
        } else {
            console.log("unsuccessful match");
            console.log(response.status);
        }
    }

    async function handleCreateHeart() {
        let response = await SubmitHeart({
            author: user_id, 
            post: post_id, 
        })

        if (response.status === 201) {
            setHeart(true);
            props.updatePosts();
        } else {
            console.log("unsuccessful heart");
            console.log(response.status);
        }

    }

    async function handleRemoveMatch() {
        let response = await RemoveMatch(post_id, user_id);

        if (response.status === 204) {
            setMatch(false); 
        } else {
            console.log('unsuccessful unmatch');
            console.log(response.status);
        }
    }

    async function handleRemoveHeart() {
        let response = await RemoveHeart(post_id, user_id);

        if (response.status === 204) {
            setHeart(false); 
            props.updatePosts();
        } else {
            console.log('unsuccessful unmatch');
            console.log(response.status);
        }
    }

    const handleMatch = () => {
        if (matched) {
            handleRemoveMatch();
        } else {
            handleCreateMatch();
        }
    }

    const handleHeart = () => {
        if (hearted) {
            handleRemoveHeart();
        } else {
            handleCreateHeart(); 
        }
    }

    if (props.isMatch) {
        return (
            <Container style={{margin: "10px"}} text>
                <Card 
                    style={{width: "100%", padding:"20px"}}
                    // href={'post/' + props.post.id + '/'}
                    centered
                >

                    <Card.Header horizontal style={{paddingLeft:"15px"}}>
                        <List horizontal>
                            <List.Item>
                                <h1 style={{fontWeight: 'bold'}}>
                                    {props.post.group.name}

                                </h1>
                            </List.Item>
                        </List>
                        <Label
                            as='a' 
                            color='blue'
                            style={{float:'right'}}
                        >
                            {props.post.group.theme}
                        </Label>

                    </Card.Header>

                    <Card.Content>
                        <Header as='h2'>
                            {props.post.content.substring(0, word_limit)}
                        </Header>
                        <Header as='h4'>
                            I want {props.post.goal} of you to join me.
                        </Header>
                        <Header>
                            <Button 
                                as={Link} 
                                labelPosition='right'
                                to={'/post/' + props.post.id}
                                >
                                <Button color='blue' 
                                    >
                                    <Icon name='handshake outline' size='large' />
                                    Match
                                </Button>
                                <Label color='blue' basic pointing='left'>
                                    {props.post.matches > 0 ? props.post.matches : 0}
                                </Label>
                            </Button>
                        </Header>
                        <small>
                            {props.post.hearts > 0 ? props.post.hearts : 0} likes | {props.post.comments.length > 0 ? props.post.comments.length : 0} comments
                        </small>

                        {/* const GroupsCard = (props) => {
    return(
        <Card>
            <Card.Content>
            <Card.Header>{props.header}</Card.Header>
            </Card.Content>
      <Card.Content extra>
        <List.Content>
          <List selection vertical>
                {props.groups.map(group => (
                  <List.Item as={Link} to={"/group/" + group.id.toString()}>
                    <Image avatar src={group.image}/>
                    <List.Content>
                      <List.Header>
                        {group.name}
                      </List.Header>
                    </List.Content>
                  </List.Item>
                ))};
            </List>
        </List.Content>
      </Card.Content>
  </Card>
  );
} */}

                    </Card.Content>

                    <Card.Content extra>
                        <List horizontal selection>
                        <Popup content='Like' basic trigger={
                            <List.Item
                                onClick={handleHeart}
                            >

                                <List.Icon 
                                name={hearted ? "heart" : "heart outline"}
                                size="large"
                                label={{as: 'a', corner: 'left', value: props.post.hearts}}
                                >

                                </List.Icon>

                            </List.Item>
                        } />
                         <Popup content='Comment' basic trigger={
                            <List.Item onClick={handleExpand}>
                                <List.Icon 

                                name={seeComments ? "comments" : 'comments outline'}

                                size='large' />
                            </List.Item>
                         } />

                                <List.Item
                                    float="right"
                                >
                                    <Image avatar src={props.post.profile.image} size="small" />
                                    <List.Content>
                                        <List.Header as='a'>
                                            {props.post.author.first_name + " " + props.post.author.last_name}
                                        </List.Header>
                                        <List.Description as='a'>
                                            {"Posted in " + props.post.group.name}
                                        </List.Description>
                                    </List.Content>
                                </List.Item>

                         {/* <Popup content='Match'  basic trigger={
                                <List.Item as={Link} to={"/post/" + props.post.id.toString()} post={props.post} style={{textDecoration: "none"}}>
                                    <List.Icon name='handshake outline' size='large' />
                                </List.Item>
                         } /> */}
                        </List>

                    </Card.Content>                    

                </Card>

                <Comments comments={props.post.comments} visible={seeComments}/>

            </Container>
        )
    } else {
        return (
        <Container text>
                <Card 
                    style={{width: "100%"}}
                    Centered
                >
                    <Card.Content>
                        <Card.Header>
                            <List horizontal selection relaxed>
                                <List.Item>
                                    <Image avatar src={props.post.profile.image} size="small" />
                                    <List.Content>
                                        <List.Header as='a'>
                                            {props.post.author.first_name + " " + props.post.author.last_name}
                                        </List.Header>
                                        <List.Description as='a'>
                                            {"Posted in " + props.post.group.name}
                                        </List.Description>
                                    </List.Content>
                                </List.Item>
                            </List>

                            <Label
                                        as='a' 
                                        color='blue'
                                        float="right"
                                    >
                                        {props.post.group.theme}
                                </Label>



                        </Card.Header>



                        <Card.Description style={{padding: 10,}}>
                            <Header as='h5'>
                                {props.post.content}
                            </Header>
                        </Card.Description>

                        <br></br>

                    </Card.Content>

                    <Card.Content extra>
                        <List horizontal selection>
                            <List.Item
                                onClick={handleHeart}
                            >
                                <List.Icon 
                                name={hearted ? "heart" : "heart outline"}
                                size="large"
                                label={{as: 'a', corner: 'left', value: props.post.hearts}}
                                >

                                </List.Icon>
                            </List.Item>
                            <List.Item onClick={handleExpand}>
                                <List.Icon 
                                name={seeComments ? "comments" : 'comments outline'} 
                                size='large' />
                            </List.Item>

                        </List>

                    </Card.Content>                    

                </Card>

                <Comments comments={props.post.comments} visible={seeComments}/>
        </Container>

        )
    }
}

const styles = {
    card: {
        width: "100%",

    },
}

export default FeedCard;
