import React, {useState} from 'react'

import {Button, Icon, Label, Progress, Card, Image, List, Container} from 'semantic-ui-react';

import Comments from "../Components/Comments";

import {Link} from "react-router-dom";



const FeedCard = (props) => {
    const [seeComments, setSeeComments] = React.useState(false);

    const handleExpand = () => {
        setSeeComments(!seeComments);
    };

    const handleMatch = () => {
        <Link to={{
            pathname: '/post',
            state: {
                post: props.post,
            }
        }}></Link>
    }


//     <Card>
//     <Card.Content header='Suggested Groups' />
//     <Card.Content extra>
//       <div class="ui middle aligned selection list">
//         {props.groups.map(group => (
//           <div class="item">
//             <img class="ui avatar image" src={group.image}/>
//             <div class="content">
//         <div class="header">{group.name}</div>
//             </div>
//           </div>
//         ))}
//       </div>
//     </Card.Content>
// </Card>


    if (props.isMatch) {
        return (
            <Container style={{margin: "10px"}}>
                <Card 
                    style={{width: "100%"}}
                >
                <Label attached="bottom">Action</Label>
                    <Card.Content>
                        <Card.Header>
                            <List horizontal selection relaxed>
                                <List.Item>
                                    <Image avatar src={props.post.profile.image} size="small" />
                                    <List.Content>
                                        <List.Header as='a'>
                                            {props.post.author.first_name + " " + props.post.author.last_name}
                                        </List.Header>
                                        <List.Description as='a'>
                                            {"Posted in " + props.post.group.name}
                                        </List.Description>
                                    </List.Content>
                                </List.Item>
                                <Label
                                        as='a' 
                                        color='blue'
                                        float="right"
                                    >
                                        {props.post.group.theme}
                                </Label>
                            </List>
                        </Card.Header>

                        <Card.Description style={{padding: 10,}}>
                            <p style={{"font-size": "15px"}}>
                            {props.post.content}
                            </p>
                        </Card.Description>

                        <br></br>

                    </Card.Content>

                    <Card.Content extra>
                        <List horizontal selection>
                            <List.Item>
                                <List.Icon 
                                name="heart outline" 
                                size="large"
                                label={{as: 'a', corner: 'left', value: props.post.hearts}}
                                >

                                </List.Icon>
                            </List.Item>
                            <List.Item onClick={handleExpand}>
                                <List.Icon name='comments outline' size='large' />
                            </List.Item>
                            <List.Item onClick={handleMatch}>
                                <List.Icon name='handshake outline' size='large' />
                            </List.Item>

                        </List>

                    </Card.Content>                    

                </Card>

                <Comments comments={props.post.comments} visible={seeComments}/>

            </Container>
        )
    } else {
        return (
        <Container>
                <Card 
                    style={{width: "100%"}}
                >
                <Label attached="bottom">Regular</Label>
                    <Card.Content>
                        <Card.Header>
                            <List horizontal selection relaxed>
                                <List.Item>
                                    <Image avatar src={props.post.profile.image} size="small" />
                                    <List.Content>
                                        <List.Header as='a'>
                                            {props.post.author.first_name + " " + props.post.author.last_name}
                                        </List.Header>
                                        <List.Description as='a'>
                                            {"Posted in " + props.post.group.name}
                                        </List.Description>
                                    </List.Content>
                                </List.Item>
                            </List>

                            <Label
                                        as='a' 
                                        color='blue'
                                        float="right"
                                    >
                                        {props.post.group.theme}
                                </Label>



                        </Card.Header>



                        <Card.Description style={{padding: 10,}}>
                            <p style={{"font-size": "15px"}}>
                            {props.post.content}
                            </p>
                        </Card.Description>

                        <br></br>

                    </Card.Content>

                    <Card.Content extra>
                        <List horizontal selection>
                            <List.Item>
                                <List.Icon 
                                name="heart outline" 
                                size="large"
                                label={{as: 'a', corner: 'left', value: props.post.hearts}}
                                >

                                </List.Icon>
                            </List.Item>
                            <List.Item onClick={handleExpand}>
                                <List.Icon name='comments outline' size='large' />
                            </List.Item>

                        </List>

                    </Card.Content>                    

                </Card>

                <Comments comments={props.post.comments} visible={seeComments}/>
        </Container>

        )
    }
}

const styles = {
    card: {
        width: "100%",

    },
}

export default FeedCard;
