import React, { Component } from "react";
import axiosInstance from "../User/axiosApi";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {username: "", password: ""};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const data = await axiosInstance.post('api/auth/token/obtain/', {
                username: this.state.username,
                password: this.state.password
            });
            axiosInstance.defaults.headers['Authorization'] = "JWT " + data.access;
            localStorage.setItem('access_token', data.access);
            localStorage.setItem('refresh_token', data.refresh);
            return data;
        } catch (error) {
            throw error;
        }
    }

    render() {
        return (
            <section>
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-12 col-md-8 col-lg-8 col-xl-6">
                            <div className="row">
                                <div className="col text-center">
                                    <h3>Log In</h3>
                                </div>
                                <form onSubmit={this.handleSubmit}>
                                    <div className="row align-items-center">
                                        <div className="col mt-4">
                                            <input type="username" className="form-control" placeholder="Username" onChange={this.handleChange} />
                                        </div>
                                    </div>
                                    <div className="row align-items-center mt-4">
                                        <div className="col">
                                            <input type="password" className="form-control" placeholder="Password" onChange={this.handleChange}/>
                                        </div>
                                    </div>
                                    <div className="row align-items-center mt-4">
                                        <div className="col">
                                            <input type="password" className="form-control" placeholder="Confirm Password" onChange={this.handleChange}/>
                                        </div>
                                    </div>
                                    <button type="submit" className="btn btn-primary btn-block">Submit</button>
                                    <p className="forgot-password text-right">
                                        Need an account? <a href="/register">Register</a>
                                    </p>
                                </form>
                            </div>    
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}

const styles = {
    cardStyle: {
        width: '50%',
    },
};


export default Login;
