    if (props.isMatch) {
        return (
            <Card style={{width: "100%"}}>
                {/* <Label as='a' color='red' ribbon>
                        Action
                </Label> */}
                <Card.Content>
                    <Card.Header>
                        <div class="ui middle aligned selection list">
                            <div class="item">
                                <img class="ui avatar image" src={props.post.profile.image} />
                                <div class="content">
                                    <div class='header'>
                                        {props.post.author.first_name + " " + props.post.author.last_name}
                                    </div>
                                    <div>
                                        <small>{"Posted in " + props.post.group.name}</small>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Card.Header>
                    <Card.Description style={{padding: 10,}}>
                        {props.post.content}
                    </Card.Description>

                    <br></br>

                    <div class="ui middle aligned selection list">
                        <div class="item">

                        </div>
                        <Button
                            content='Heart'
                            icon='heart'
                            label={{ as: 'a', basic: true, content: props.post.hearts }}      
                            labelPosition = 'right' /> 

                        <Button
                            content='Match'
                            icon='handshake outline'
                            label={{ as: 'a', basic:true, content: props.post.matches }}
                            labelPosition = 'right'
                        />                

                    </div>

                </Card.Content>
                <Card.Content extra>
                    <Progress 

                    size="tiny"

                    percent={((props.post.matches / props.post.goal) * 100).toFixed(3)} 

                    autoSuccess 

                    label = {"Help me hit my goal of " + props.post.goal + "actions!"}

                    />

                </Card.Content>                    
            </Card>
        )
    } else {
        return (
            <div>test</div>
        )
    }
}

const styles = {
    card: {
        width: "100%",
        height: "50%",
    },
}

