import React, { Component, useState, useEffect } from "react";
import { render } from "react-dom";
import axiosInstance from "../User/axiosApi";
import GroupInfo from "../Components/GroupInfo";
import UserInfo from "../Components/UserInfo";
import ProfileInfo from "../Components/ProfileInfo";


function Posts() {
    const [posts, setPosts] = useState([]);
    const [matches, setMatches] = useState([]); 

    useEffect(() => {
        postFetch({ limit:10 });
    }, []);    

    async function parsePosts(data) {
        let posts = data.filter(post => post.isMatch === false);
        let matches = data.filter(post => post.isMatch === true);

        for (let idx in posts) {
            posts[idx].group = await GroupInfo(posts[idx].group);
            posts[idx]['user_id'] = posts[idx].author; 
            posts[idx]['profile'] = await ProfileInfo(posts[idx].user_id);
            posts[idx].author = await UserInfo(posts[idx].user_id);
        }
        for (let idx in matches) {
            matches[idx].group = await GroupInfo(matches[idx].group);
            matches[idx]['user_id'] = matches[idx].author;
            matches[idx]['profile'] = await ProfileInfo(matches[idx].user_id);
            matches[idx].author = await UserInfo(matches[idx].user_id);
        }

        setPosts(posts);
        setMatches(matches);

    }

    async function postFetch(pagination) {
        try {
            var limit = pagination.limit; 
            var id = localStorage.getItem("user_id");
            const response = await axiosInstance.get('api/posts/interests/' + id.toString() + "/" + limit.toString() + "/");

            const data = response.data;

            await parsePosts(data);

            return response.data; 
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div>
            {/* <InfiniteScroll
                pageStart = {0}
                loadMore = {() => postFetch({ limit: posts.length + matches.length + 10 })}
                hasMore = {true || false}
                useWindow = {false}
                loader = {
                    <div key="loading" className="loader">
                        Loading...
                    </div>
                }
            >  */}
                {matches.map(match => (
                    <div>
                        <h1>MATCH POST</h1>
                        {match.content}
                        {match.hearts}
                        {match.author.first_name}
                        {match.author.last_name}
                        {match.group.name}
                        {match.group.description}
                        {match.matches}
                        {match.goal}
                    </div>
                ))

                }
                {posts.map(post => (
                    <div>
                        <h1>REGULAR POST</h1>
                        {post.content}
                        {post.hearts}
                        {post.author.first_name}
                        {post.author.last_name}
                        {post.group.name}
                        {post.group.description}
                    </div>
                ))}
            {/* </InfiniteScroll> */}
        </div>
    );

}

export default Posts;
