import React, {Component} from 'react'
import NavBar from "../Components/NavBar";
import axiosInstance from "../User/axiosApi";
import { Icon, Card, Grid} from 'semantic-ui-react'
import { BsFillLightningFill} from "react-icons/bs";
import {FaHandsHelping} from "react-icons/fa"
import * as Color from "../Components/Color";
import UserGroups from "../APIComponents/UserGroups";
import ProfileInfo from "../APIComponents/ProfileInfo";
import UserInfo from "../APIComponents/UserInfo";
import UserPosts from "../APIComponents/UserPosts";
import ParsePosts from "../APIComponents/ParsePosts";
import UserComments from "../APIComponents/UserComments";
import ParseComments from "../APIComponents/ParseComments";

const description = [
  'I am a violinist with 2 years of experience in the wedding industry.',
  'I enjoy the outdoors and currently reside in upstate New York.',
].join(' ')

// actions inspired, actions matched 
// image 
// groups -> group images
// user posts / comments  


class Profile extends Component {
  constructor() {
    super();
    this.handleLogout = this.handleLogout.bind(this);
    this.state = {
      groups: [], // group. name, description, image 
      user: {}, // user.first_name, user.last_name
      profile: {}, // profile. bio, image, ind_matches, total_matches 
      posts: [], // post. content, group, author, hearts, matches, isMatch, goal
      comments: [], // comment. content, post 
    };
  }

  componentDidMount() {
    const user_id = localStorage.getItem('user_id');

    this.setPosts(user_id);
    this.setUser(user_id); // can likely replace some of these with functional progr. // no time 
    this.setGroups(user_id);
    this.setProfile(user_id); 
    this.setComments(user_id); 
  }

  async setPosts(user_id) {
    let posts = await UserPosts(user_id);
    posts = ParsePosts(posts);

    console.log("POSTS");
    console.log(posts);

    let obj = this.state;
    obj.posts = posts.data; 
    this.setState(obj);

    console.log(obj); 
  };

  async setUser(user_id) {
    let user = await UserInfo(user_id);

    console.log("USER");
    console.log(user);

    let obj = this.state; 
    obj.user = user; 
    this.setState(obj);
  };

  async setGroups(user_id) {
    let groups = await UserGroups(user_id);

    console.log("GROUPS");
    console.log(groups);

    let obj = this.state;
    obj.groups = groups; 
    this.setState(obj); 
  }

  async setProfile(user_id) {
    let profile = await ProfileInfo(user_id);

    console.log("PROFILE");
    console.log(profile);

    let obj = this.state; 
    obj.profile = profile; 
    this.setState(obj); 
  }

  async setComments(user_id) {
    let comments = await UserComments(user_id);
    comments = ParseComments(comments);

    console.log("COMMENT");
    console.log(comments);

    let obj = this.state; 
    obj.comments = comments;
    this.setState(obj); 
  }

  async handleLogout() {
      try {
          const response = await axiosInstance.post('/blacklist/', {
              "refresh_token": localStorage.getItem("refresh_token")
          });
          localStorage.removeItem('access_token');
          localStorage.removeItem('refresh_token');
          axiosInstance.defaults.headers['Authorization'] = null;
          return response;
      }
      catch (e) {
          console.log(e);
      }
  };

  render (){
    return (
      <div>
        <NavBar />

        {/* {this.state.profile.image}
        {this.state.user.first_name}
        {this.state.groups.map(group => (
          <div>
            <h1>GROUP</h1>
            {group.name}
            {group.description}
          </div>
        ))}
        {this.state.posts.map(post => (
          <div>
            <h1>POST</h1>
            {post.content}
          </div>
        ))}
        {this.state.comments.map(comment => (
          <div>
            <h1>COMMENT</h1>
            {comment.content}
            {comment.post.author.first_name}
          </div>
        ))} */}

        <Card style={{width: "55%", height: 430, marginRight: "auto", marginLeft: "auto"}}>
          <Grid>
            <Grid.Row style={{marginTop: 20, height: 250}}>
              <Grid.Column width={2}></Grid.Column>
              <Grid.Column width={4} style={{left: -25}}>

              </Grid.Column>
              <Grid.Column width={5} style={{top: 255, fontSize: 17, height: 30, left: 50}}>
                <a style={{paddingRight: 6}}><BsFillLightningFill size={18} style={{top: -10}}/></a>
                  Actions Inspired: {this.props.inspired}
              </Grid.Column>
              <Grid.Column width={5} style={{top: 255, fontSize: 17, left: 20}}>
              <a style={{paddingRight: 6}}><FaHandsHelping size={18} style={{top: -10}}/></a>
                Actions Matched: {this.props.matched}
              </Grid.Column>
            </Grid.Row>
            <Grid.Row>
              <Grid.Column width={2}></Grid.Column>
              <Grid.Column width={5} style={{left: -20, fontSize: 25}}>
                {this.props.username}
              </Grid.Column>
            </Grid.Row>
            <Grid.Row>
              <Grid.Column width={2}></Grid.Column>
              <Grid.Column width={13} style={{left: -20, fontSize: 18}}>
              {description}
              </Grid.Column>
            </Grid.Row>
          </Grid>
          <img src={this.props.src}  style={{width:240, position: "absolute", left: 40, top: 18}}/>
          <Grid.Column width={6} style={{fontSize: 16, position: "absolute", right: 135, top: 85}}>
                <Grid.Row style={{fontSize: 20, paddingBottom: 10}}>
                  <div style={{paddingLeft: 110}}>
                  Groups
                  </div>

                </Grid.Row>
                  {/* {handleGroups(this.props.groups)} */}
                  <img className="bestGroup" src={require("../Images/simplecharity.png")} style={{width: 75, marginRight: 15}}/>
                  <img src={require("../Images/dementia.png")} style={{width: 75,  marginRight: 15}}/>
                  <img src={require("../Images/hiking.png")} style={{width: 75}}/>
              </Grid.Column>
        </Card>


      </div>

    );
  }
}

export default Profile;
