import React, {useEffect} from "react";
import { Button, Container, Image, List, Grid, Segment, Header, Divider, Step, Form, Table, Icon, Modal } from "semantic-ui-react";
import {Link} from "react-router-dom";
import Login from "./Login"
import Register from "./Register";
import LandingNav from "../Components/LandingNav";
import PopularPosts from "../APIComponents/PopularPosts";
import ParsePosts from "../APIComponents/ParsePosts";
import Posts from "../Components/Posts";

const steps = [
    {
        key: 'unite',
        icon: 'group',
        title: 'Unite.',
        description: 'Create or find your community.',
    },
    {
        key: 'act',
        icon: 'bullhorn',
        title: 'Act.',
        description: 'Lead with your actions.',
    },
    {
        key: 'grow',
        icon: 'handshake outline',
        title: 'Grow.',
        description: 'Grow with others as they match your actions.'
    },
]

const Landing = (props) => {

    const [posts, setPosts] = React.useState([]);
    const [openLogin, setOpenLogin] = React.useState(false)
    const [openRegister, setOpenRegister] = React.useState(false)

    useEffect(() => {
        getTrending();
    }, {});

    async function getTrending() {
        let posts = await PopularPosts();
        posts = ParsePosts(posts);
        posts.then((data) => {
            setPosts(data);
        })
    }

    return (
        <Container>

            <LandingNav />

            <Segment style={{ padding: '2em 0em' }} vertical>


                <Container textAlign = "center">
                    <Header as='h1' style={{ fontSize: '3em' }}>
                        Change the world with confidence. 
                    </Header>
                    <p style={{ fontSize: '1.5em' }}>
                        We help you find your community.
                    </p>

                    <Step.Group items={steps}/>

                </Container>


                <Modal
                onClose={() => setOpenLogin(false)}
                onOpen={() => setOpenLogin(true)}
                open={openLogin}
                trigger={
                    <Container textAlign = "center" style={{ padding: "5em 0em"}}>
                        <Button content="Join" size="big"/>
                    </Container>
                    }
                    size="tiny"
                >
                    <Modal.Content>



                        <Login close={() => {setOpenLogin(false)}}/>
                        <p className="forgot-password text-right" style={{marginTop: 20, marginRight: 72}}>
                                Need an account? <a href="#" onClick={() => {
                                    setOpenLogin(false)
                                    setOpenRegister(true)}}>Register</a>
                        </p>



                    </Modal.Content>
                </Modal>

                <Modal
                onClose={() => setOpenRegister(false)}
                onOpen={() => setOpenRegister(true)}
                open={openRegister}
                size="tiny"
                >
                    <Modal.Content>
                        <Register />
                        <p className="forgot-password text-right"  style={{marginTop: 20, marginRight: 72}}>
                            Already a member? <a href="#" onClick={() => {
                                setOpenRegister(false)
                                setOpenLogin(true)}}>Login</a>
                        </p>
                    </Modal.Content>

                </Modal>


                <Container textAlign="center">
                    <Header as='h1' style={{fontSize: '2em'}}>
                        Trending Now
                    </Header>


                    <Posts posts={posts}/>


                </Container>




            </Segment>

            {/* <Segment style={{ padding: '8em 0em' }} vertical>
            <Grid container stackable verticalAlign='middle'>
                <Grid.Row>
                <Grid.Column width={8}>
                    <Header as='h1' style={{ fontSize: '2.5em' }}>
                        Unite
                    </Header>
                    <p style={{ fontSize: '1.33em' }}>
                    We can give your company superpowers to do things that they never thought possible.
                    Let us delight your customers and empower your needs... through pure data analytics.
                    </p>
                </Grid.Column>
                <Grid.Column floated='right' width={6}>
                    <Image bordered rounded size='large' src='/images/wireframe/white-image.png' />
                </Grid.Column>
                </Grid.Row>
                <Grid.Row>
                <Grid.Column textAlign='center'>
                    <Button size='huge'>Check Them Out</Button>
                </Grid.Column>
                </Grid.Row>
            </Grid>
            </Segment> */}


        </Container>

    )
}

export default Landing;
