import React, { Component, useState, useEffect } from "react";
import { render } from "react-dom";
import axiosInstance from "../User/axiosApi";
import InfiniteScroll from 'react-infinite-scroller';


function PostInfScroll() {
    const [posts, setPosts] = useState();
    const [matches, setMatches] = useState(); 

    useEffect(() => {
        postFetch({ limit:10 });
    }, []);    

    async function postFetch(pagination) {
        try {
            var limit = pagination.limit; 
            var id = localStorage.get("user_id");
            const response = await axiosInstance.get('posts/interests/' + id.toString() + "/" + limit.toString() + "/");

            const data = response.data.json();

            setPosts(data.filter(post => post.isMatch === false));
            setMatches(data.filter(post => post.isMatch === true));

            return response.data; 
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div>
            <InfiniteScroll
                pageStart = {0}
                loadMore = {() => postFetch({ limit: posts.length + matches.length + 10 })}
                hasMore = {true || false}
                useWindow = {false}
                loader = {
                    <div key="loading" className="loader">
                        Loading...
                    </div>
                }
            > 
                {matches.map(match => (
                    <div>
                        {match.content}
                        {match.total_hearts}
                        {match.author}
                        {match.group}
                        {match.total_matches}
                        {match.goal}
                    </div>
                ))

                }
                {posts.map(post => (
                    <div>
                        {post.content}
                        {post.total_hearts}
                        {post.author}
                        {post.group}
                    </div>
                ))}
            </InfiniteScroll>
        </div>
    );

}

export default PostInfScroll;
