import React, { Component } from "react";
import axiosInstance from "../User/axiosApi";

class Register extends Component{
    constructor(props){
        super(props);
        this.state = {
            username: "",
            first_name: "",
            last_name: "",
            password: "",
            email:"",
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('api/auth/register/', {
                username: this.state.username,
                email: this.state.email,
                password: this.state.password,
                first_name: this.state.first_name,
                last_name: this.state.last_name
            });
            if (response.status === 200) {

            }

            return response;
        } catch (error) {
            console.log(error.stack);
            this.setState({
                errors:error.response.data
            });
        }
    }

    render() {
        return (
            <div class="container justify-content-center align-items-center">
                <div class="row justify-content-center">
                    <div class="col-md-6" style={styles.register_form}>
                        <h3>Register</h3>
                        <form onSubmit={this.handleSubmit}>
                            <div class="form-group">
                                <input type="username" className="form-control" placeholder="Username" onChange={this.handleChange} value={this.state.username}/>
                            </div>
                            <div class="form-group">
                                <input type="password" className="form-control" placeholder="First Name" onChange={this.handleChange} value={this.state.first_name}/>
                            </div>
                            <div class="form-group">
                                <input type="password" className="form-control" placeholder="Last Name" onChange={this.handleChange} value={this.state.last_name}/>
                            </div>
                            <div class="form-group">
                                <input type="password" className="form-control" placeholder="Email" onChange={this.handleChange} value={this.state.email}/>
                            </div>
                            <div class="form-group">
                                <input type="password" className="form-control" placeholder="Password" onChange={this.handleChange} value={this.state.password}/>
                            </div>
                            <button type="submit" value="submit" className="btn btn-primary btn-block">Sign Up</button>
                            <p className="forgot-password text-right">
                                Already registered? <a href="/login">Sign in</a>
                            </p>
                        </form>
                    </div>
                </div>
            </div>    
        )
    }
}

const styles = {
    register_form: {
        "box-shadow": "0 5px 8px 0 rgba(0, 0, 0, 0.2), 0 9px 26px 0 rgba(0, 0, 0, 0.19);"
    },
};

export default Register;
