import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import NavBar from "../Components/NavBar";
import ParsePost from "../APIComponents/ParsePost";
import PostInfo from "../APIComponents/PostInfo";
import {Grid, Container, Header, Image, Label} from "semantic-ui-react";

const IndPost = (props) => {

    const [post, setPost] = useState({});
    const post_id = props.postID;

    useEffect(() => {
        getPost();
    }, {});

    async function getPost() {
        let data = await PostInfo(post_id);
        console.log(data);
        data = await ParsePost(data);
        setPost(data);
    }
    if (post.isMatch) {
        return(
            <div>
                <NavBar />
                <Grid centered columns={2}>
                    <Grid.Column>
                        <Container text>
                            <Label as='a' color='blue' ribbon>
                                {post.group.theme}
                            </Label>
                            <Header as ='h1' style={{fontSize: '5em'}}>
                                {post.group.name}
                            </Header>
                            <Header as='h1'>
                                Match {post.author.first_name}'s lead.
                            </Header>
                        </Container>
                    </Grid.Column>
                    <Grid.Row centered columns={4}>
                        <Grid.Column>
                            <Container text>
                                <p>
                                    {post.content}
                                </p>
                            </Container>
                        </Grid.Column>
                        <Grid.Column>

                        </Grid.Column>
                    </Grid.Row>
                </Grid>
            </div>
        )
    } else {
        return(
            <div>
                under construction
            </div>
        )
    }

}

export default IndPost;
